Alias.script:
local count = 0 notify("System Characters")
for i = 1, 255 do if i % 4 == 0 then echo("\n") end
echo(string.jleft(i .. ": " .. string.char(i), 20)) end send("\n")

local reports = {
	["c"] = "credit",
	["i"] = "ironcoin",
	["cu"] = "currency",
}

send(reports[matches[2]] .. " report", false)

